#!/usr/bin/env python
# -*- coding: utf-8 -*-

from pprint import pprint

def show_info(obj, is_class=False):
    """
    Show docstring and attributes for input function.
    """
    if is_class:
        string = 'class'
    else:
        string = 'method'
    print("====== START %s information ======" % string)
    print("")
    print("# ---------")
    print("# docstring")
    print("# ---------")
    print(obj.__doc__)
    print("")
    if is_class:
        print("# ---------------")
        print("# attribute names")
        print("# ---------------")
        pprint(list(obj.__dict__.keys()))
        print("")
        print("# ------")
        print("# values")
        print("# ------")
        pprint(obj.__dict__)
        print("")
    print("======= END %s information =======" % string)
    print("")
    print("")
